import React from "react";

export default class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  taskTitleChange(event) {
    this.setState({value: event.target.value});
  }

  taskDescChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('追加するわよ〜 : ' + this.state.value);
    fetch("http://localhost:4000/insert")
      .then(response => response.json())
      .then(posts => this.setState({ posts }));
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Tastkitle:
          <input type="text" value={this.state.value} onChange={this.taskTitleChange} />
        </label>
        <label>
          TaskDesc:
          <input type="text" value={this.state.value} onChange={this.taskDescChange} />
        </label>
        <input type="submit" value="追加！" />
      </form>
    );
  }
};
