import React from "react";
import request from 'superagent';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [] };

    this.categoryDefault = this.categoryDefault.bind(this);
    this.categoryChange = this.categoryChange.bind(this);

    request
      .get('http://localhost:4000/posts')
      .end((err, res) => {
        var posts = res.body;
        this.setState({ posts });
      });

  }

  handleDelete(event) {
    request
      .post('http://localhost:4000/delete')
      .type('form')
      .send({
        id: event.target.value
      })
      .end(() => {
        alert("タスクを完了しました");
        window.location.reload();
      });
  }

  categoryDefault(event){
    request
      .get('http://localhost:4000/posts')
      .end((err, res) => {
        var posts = res.body;
        this.setState({posts: posts});
      });
  }

  categoryChange(event){
    request
      .post('http://localhost:4000/posts/category')
      .type('form')
      .send({
        category: event.target.value
      })
      .end((err, res) => {
        var posts = res.body;
        this.setState({posts: posts});
      });
  }

  render() {
    return (
      <Router>
        <div>
          <Route path='/' exact component={topPage}/>
          <Route path='/page1' exact component={page1}/>
          <Route path='/page2' exact component={page2}/>
          <Route path='/page3' exact component={page3}/>
        </div>
      </Router>
    );
  }
}


const topPage = () => 
  <div>
    <h1>MY TASKs</h1>
    <button type="submit" onClick={this.categoryDefault}>全てのタスクを表示する</button>
    <button type="submit" value="1" onClick={this.categoryChange}>納品自動化</button>
    <button type="submit" value="2" onClick={this.categoryChange}>課金処理の検証</button>
    <button type="submit" value="3" onClick={this.categoryChange}>Reactの勉強</button>
    <ul>
      {this.state.posts.map(post => (
        <li　key={post.id}>
          <h3>{post.title}</h3>
          <p>{post.content}</p>
          <p>{post.limit_date}</p>
          <button type="submit" value={post.id} onClick={this.handleDelete}>EDIT</button>
          <button type="submit" value={post.id} onClick={this.handleDelete}>DONE</button>
          <button type="submit" value={post.id} onClick={this.handleDelete}>STASH</button>
        </li>
      ))}
    </ul>
  </div>
const page1 = () => <div><h1>page1</h1>1枚目のページです</div>
const page2 = () => <div><h1>page2</h1>2枚目のページです</div>
const page3 = () => <div><h1>page3</h1>3枚目のページです</div>



