import React from "react";
import request from 'superagent';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

const list = {
    textDecoration: 'none',
    color:'#000000',
    borderStyle: 'none'
};

export default class Category extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      categorys: []
    };

    request
        .get('http://localhost:4000/categorys')
        .end((err, res) => {
            var categorys = res.body;
            this.setState({ categorys });
        });
  }

  handleDelete(event) {
    request
      .post('http://localhost:4000/category/delete')
      .type('form')
      .send({
        id: event.target.value
      })
      .end(() => {
        window.location.reload();
      });
  }

  render() {
    return (
      <div class="category-editer-wrapper">
        <Link to="/" style={list}>Cansel</Link>
        <Link to="/category/create" style={list}>NEW</Link>
        <ul>
            {this.state.categorys.map(category => (
                <liã€€key={category.id}>
                    <h3>{category.title}</h3>
                    <button><Link to={'category/edit/' + category.id} style={list}>EDIT</Link></button>
                    <button type="submit" value={category.id} onClick={this.handleDelete}>STASH</button>
                </li>
            ))}
        </ul>
      </div>
    );
    document.getElementById('calendar-button')
  }
};
