import React from "react";
import request from 'superagent';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

export default class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        posts: [],
        categorys: []
    };

    this.categoryDefault = this.categoryDefault.bind(this);
    this.handleChangeStatus = this.handleChangeStatus.bind(this);
    this.categoryChange = this.categoryChange.bind(this);

    request
      .get('http://localhost:4000/posts')
      .end((err, res) => {
        var posts = res.body;
        this.setState({ posts });
      });

    request
        .get('http://localhost:4000/categorys')
        .end((err, res) => {
            var categorys = res.body;
            this.setState({ categorys });
        });

  }

  handleDelete(event) {
    request
      .post('http://localhost:4000/delete')
      .type('form')
      .send({
        id: event.target.value
      })
      .end(() => {
        window.location.reload();
      });
  }

  categoryDefault(event){
    request
      .get('http://localhost:4000/posts')
      .end((err, res) => {
        var posts = res.body;
        this.setState({posts: posts});
      });
  }

  categoryChange(event){
    request
      .post('http://localhost:4000/posts/category')
      .type('form')
      .send({
        category: event.target.value
      })
      .end((err, res) => {
        var posts = res.body;
        this.setState({posts: posts});
      });
  }

  handleChangeStatus(event){
    var result = event.target.value.split('_')
    var status = result[0];
    var id = result[1];
    var changeStatus = 0;
    if (status == 0) {
        changeStatus = 1;
    } else if (status == 1) {
        changeStatus = 0;
    }
    request
      .post('http://localhost:4000/update/status')
      .type('form')
      .send({
        status: changeStatus,
        id: id
      })
      .end((err, res) => {
        window.location.reload();
      });
  }

  render() {
    return (
        <div>
            <div class="category-wrapper">
                <ul>
                    <li>
                        <button type="submit" onClick={this.categoryDefault}>全てのタスクを表示する</button>
                    </li>
                    {this.state.categorys.map(category => (
                        <li>
                            <button type="submit" value={category.id} onClick={this.categoryChange}>{category.title}</button>
                        </li>
                    ))}
                    <li>
                        <Link to="/category">+ EDIT CATEGORY</Link>
                    </li>
                </ul>
            </div>
            <div class="tasklist-wrapper">
                <ul>
                    <li class="task-wrapper new">
                        <div class="task-content">
                            <Link to="/create">+ NEW TASK</Link>
                        </div>
                    </li>
                    {this.state.posts.map(post => (
                        <li　key={post.id} class="task-wrapper">
                        { post.status === 0 && 
                            <div class="task-content yet">
                                <h3>{post.title}</h3>
                                <p class="content">{post.content}</p>
                                <p class="limit_date yet">{post.limit_date}</p>
                            </div> }
                        { post.status === 1 && 
                            <div class="task-content done">
                                <h3>{post.title}</h3>
                                <p class="content">{post.content}</p>
                                <p class="limit_date done">{post.limit_date}</p>
                            </div> }
                            <div class="task-buttons">
                                <ul>
                                    <li>
                                        <img src="../images/edit.jpeg" />
                                        {/* <button class="edit"><Link to={'/edit/' + post.id}></Link></button> */}
                                    </li>
                                    <li>
                                        <button type="submit" value={post.status + '_' + post.id} onClick={this.handleChangeStatus}>DONE</button>
                                    </li>
                                    <li>
                                        <button type="submit" value={post.id} onClick={this.handleDelete}>STASH</button>
                                    </li>
                                </ul>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
  }
}
