import React from "react";
import request from 'superagent';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [] };

    request
      .get('http://localhost:4000/posts')
      .end((err, res) => {
        var posts = res.body;
        this.setState({ posts });
      });

  }

  handleDelete(event) {
    request
      .post('http://localhost:4000/delete')
      .type('form')
      .send({
        id: event.target.value
      })
      .end(() => {
        alert("タスクを完了しました");
        window.location.reload();
      });
  }

  categoryChange(event){
    console.log(event.target.value);
    request
      .get('http://localhost:4000/posts/category')
      .type('form')
      .send({
        category: event.target.value
      })
      .end((err, res) => {
        console.log(res);
        // var posts = res.body;
        // this.setState({ posts });
      });
  }

  render() {
    return (
      <div>
        <h1>MY TASKs</h1>
        <button type="submit" value="0" onClick={this.categoryChange}>納品自動化</button>
        <button type="submit" value="1" onClick={this.categoryChange}>課金処理の検証</button>
        <button type="submit" value="2" onClick={this.categoryChange}>Reactの勉強</button>
        <ul>
          {this.state.posts.map(post => (
            <li>
              <h2>{post.title}</h2>
              <p>{post.content}</p>
              <p>{post.limit_date}</p>
              <button type="submit" value={post.id} onClick={this.handleDelete}>✔️</button>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
