import React from "react";
import request from 'superagent';
import DatePicker from "react-datepicker";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [] };

    this.categoryDefault = this.categoryDefault.bind(this);
    this.categoryChange = this.categoryChange.bind(this);

    request
      .post('http://localhost:4000/posts/edit')
      .type('form')
      .send({
        id: this.props.match.params.id
      })
      .end(() => {
        alert("タスクを完了しました");
        window.location.reload();
      });

  }

  render() {
    return (
      <div>
        <Link to="/">Cansel</Link>
          <h1>EDIT</h1>
        <form onSubmit={this.handleSubmit}>
          <label>
            TaskTitle:
            <input type="text" id="title" name="title" value={this.state.title} onChange={this.taskTitleChange} />
          </label>
          <label>
            TaskDesc:
            <input type="text" id="content" name="content" value={this.state.content} onChange={this.taskContentChange} />
          </label>
          <label>
            TaskCategory:
            <select
            value={this.state.category}
            onChange={ e => this.setState({category: e.target.value}) }>
              <option value="0">指定なし</option>
              <option value="1">納品自動化</option>
              <option value="2">特価200C施策</option>
              <option value="3">Reactの勉強</option>
            </select>
          </label>
          <label>
            LimitDate:
            <DatePicker
              selected={this.state.limit}
              onChange={this.taskLimitChange}
              dateFormat="yyyy-MM-dd"
            />
          </label>
          <input type="submit" value="追加！" />
        </form>
      </div>
    );
  }
}
