import React from "react";
import request from 'superagent';
import DatePicker from 'react-ja-date-picker'

export default class Form extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: 'やること',
      content: 'やること詳細',
      limit: 'yyyy-MM-dd'
    };

    this.taskTitleChange = this.taskTitleChange.bind(this);
    this.taskContentChange = this.taskContentChange.bind(this);
    this.taskLimitChange = this.taskLimitChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentWillReceiveProps(nextProps){
    if(nextProps.editData){
       this.setState({
        title: nextProps.editData.title,
        content: nextProps.editData.content,
        limit: nextProps.editData.limit,
       });
    }
 }

  taskTitleChange(event) {
    this.setState({title: event.target.value});
  }

  taskContentChange(event) {
    this.setState({content: event.target.value});
  }

  taskLimitChange(event) {
    this.setState({limit: event.target.value});
  }

  taskLimitSelect(event) {
    this.setState({limit: event.target.onSelect.year + event.target.onSelect.month + event.target.onSelect.day});
  }

  handleSubmit(event) {
    request
      .post('http://localhost:4000/insert')
      .type('form')
      .send({
        type: this.props.status,
        title: this.state.title,
        content: this.state.content,
        limit: this.state.limit
      })
      .end((err, res) => {
      });
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          TaskTitle:
          <input type="text" id="title" name="title" value={this.state.title} onChange={this.taskTitleChange} />
        </label>
        <label>
          TaskDesc:
          <input type="text" id="content" name="content" value={this.state.content} onChange={this.taskContentChange} />
        </label>
        <label>
          LimitDate:
          <input type="text" id="limit" name="limit" value={this.state.limit} onChange={this.taskLimitChange} />
        </label>
        <DatePicker
          date={ this.state.limit }
          type={ 'input' }
          onSelect={ (year, month, day) => this.taskLimitSelect }
        />
        <input type="submit" value="追加！" />
      </form>
    );
    document.getElementById('calendar-button')
  }
};
