import React from "react";
import request from 'superagent';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [] };

    this.categoryDefault = this.categoryDefault.bind(this);
    this.handleChangeStatus = this.handleChangeStatus.bind(this);
    this.categoryChange = this.categoryChange.bind(this);

    request
      .get('http://localhost:4000/posts')
      .end((err, res) => {
        var posts = res.body;
        this.setState({ posts });
      });

  }

  handleDelete(event) {
    request
      .post('http://localhost:4000/delete')
      .type('form')
      .send({
        id: event.target.value
      })
      .end(() => {
        alert("タスクを完了しました");
        window.location.reload();
      });
  }

  categoryDefault(event){
    request
      .get('http://localhost:4000/posts')
      .end((err, res) => {
        var posts = res.body;
        this.setState({posts: posts});
      });
  }

  categoryChange(event){
    request
      .post('http://localhost:4000/posts/category')
      .type('form')
      .send({
        category: event.target.value
      })
      .end((err, res) => {
        var posts = res.body;
        this.setState({posts: posts});
      });
  }

  handleChangeStatus(event){
    console.log(event.target.value);
    console.log(event.target.value.status);
    var changeStatus = 0;
    if (event.target.value.status === 0) {
        changeStatus = 1;
    } else if (event.target.value.status === 1) {
        changeStatus = 0;
    }
    console.log(changeStatus);
    // request
    //   .post('http://localhost:4000/update/status')
    //   .type('form')
    //   .send({
    //     status: changeStatus,
    //     id: event.target.value.id
    //   })
    //   .end((err, res) => {
    //     var posts = res.body;
    //     this.setState({posts: posts});
    //   });
  }

  render() {
    return (
        <div>
            <h1>MY TASKs</h1>
            <Link to="/create">NEW</Link>
            <button type="submit" onClick={this.categoryDefault}>全てのタスクを表示する</button>
            <button type="submit" value="1" onClick={this.categoryChange}>納品自動化</button>
            <button type="submit" value="2" onClick={this.categoryChange}>課金処理の検証</button>
            <button type="submit" value="3" onClick={this.categoryChange}>Reactの勉強</button>
            <ul>
            {this.state.posts.map(post => (
                <li　key={post.id}>
                <h3>{post.title}</h3>
                <p>{post.content}</p>
                <p>{post.limit_date}</p>
                { post.status === 0 && <p>YET</p> }
                { post.status === 1 && <p>DONE</p> }
                <button><Link to={'/edit/' + post.id}>EDIT</Link></button>
                <button type="submit" value={post.id} onClick={this.handleChangeStatus}>DONE</button>
                <button type="submit" value={post} onClick={this.handleDelete}>STASH</button>
                </li>
            ))}
            </ul>
        </div>
    );
  }
}
