import React from "react";
import request from 'superagent';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

export default class Category extends React.Component {
  constructor(props) {
    super(props);
    console.log(this.props.match.params.id);

    this.state = {
      category: '',
      id: 0
    };

    request
        .post('http://localhost:4000/categorys/category')
        .type('form')
        .send({
          id: this.props.match.params.id
        })
        .end((err, res) => {
          console.log(res.body);
        });
  }

  categoryTitleChange(event) {
    this.setState({category: event});
  }

  handleSubmit(event) {
    request
      .post('http://localhost:4000/category/update')
      .type('form')
      .send({
        id: this.state.id,
        category: this.state.category
      })
      .end((err, res) => {
        alert("カテゴリーを変更しました");
      });
  }

  render() {
    return (
      <div>
        <Link to="/category">Cansel</Link>
        <form onSubmit={this.handleSubmit}>
          <label>
            CategoryTitle:
            <input type="text" id="category" name="category" value={this.state.category} onChange={this.categoryTitleChange} />
          </label>
          <input type="submit" value="変更" />
        </form>
      </div>
    );
    document.getElementById('calendar-button')
  }
};
