import React from "react";
import request from 'superagent';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [] };

    fetch("http://localhost:4000/posts")
      .then(response => response.json())
      .then(posts => this.setState({ posts }));
  }

  handleDelete(event) {
    request
      .post('http://localhost:4000/delete')
      .type('form')
      .send({
        id: event.target.value
      })
      .end(() => {
        alert("タスクを完了しました");
        window.location.reload();
      });
  }

  categoryChange(event){
    if (event.target.value = "0") {
      fetch("http://localhost:4000/posts/today")
        .then(response => response.json())
        .then(posts => this.setState({ posts }));
    } else if (event.target.value = "1") {
      fetch("http://localhost:4000/posts")
        .then(response => response.json())
        .then(posts => this.setState({ posts }));
    }
  }

  render() {
    return (
      <div>
        <h1>MY TASKs</h1>
        <button type="submit" value="0" onClick={this.categoryChange}>TODAY's TASKs</button>
        <button type="submit" value="1" onClick={this.categoryChange}>ALL TASKs</button>
        <ul>
          {this.state.posts.map(post => (
            <li>
              <h2>{post.title}</h2>
              <p>{post.content}</p>
              <p>{post.limit_date}</p>
              <button type="submit" value={post.id} onClick={this.handleDelete}>✔️</button>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
